      {"path":"/img/1.jpeg"},
      {"path":"/img/2.jpeg"},
      {"path":"/img/3.jpeg"},
      {"path":"/img/4.jpeg"},
      {"path":"/img/5.jpeg"},
      {"path":"/img/6.jpeg"},
      {"path":"/img/7.jpeg"},
      {"path":"/img/8.jpeg"}
    ]

function App() {
  const [cards,setCards] = useState([]);

  const prepareCards = ()=>{
    setCards([...cardList,...cardList])
  }

  useEffect(()=>{
    prepareCards()
  },[])

  return (
    <div className="container">
      <h1>Memory App</h1>
      <button>Oyunu Ba≈ülat</button>
      <div className="card-grid">
        {
           cards.map(card => (
              <MemoryCard/>
          ))
        }
      </div>
    </div>
  );
}

export default App;
