import React, { useState } from 'react'
import "./Create.css"

function Create() {
  const [baslik,setBaslik] = useState("")
  const [aciklama,setAciklama] = useState("")
  const [hazirlanisi,setHazirlanisi] = useState("")
  const [resim,setResim] = useState("")
  const [malzeme,setMalzeme] = useState("")
  const [malzemeler,setMalzemeler] = useState("")
  const [url,setUrl] = useState("")

  const handleAddMalzeme = (e) =>{
    const item = malzeme.trim()
    if(item && !malzemeler.includes(item)){
      setMalzemeler(prevItem=> [...prevItem,item])
    }
  }

  const handleSubmit = (e)=>{
    e.preventDefault()
    console.log(baslik,aciklama,hazirlanisi,resim,url)
  }

  return (
    <div className="card mt-3">
      <div className="card-body">
        <h2>Yeni Tarif Ekle</h2>
        <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="baslik" className='form-label'>Başlık</label>
              <input type="text" className='form-control' id='baslik' name='baslik' onChange={(e)=>setBaslik(e.target.value)}/>
            </div>
            <div className="mb-3">
              <label htmlFor="aciklama" className='form-label'>Açıklama</label>
              <input type="text" className='form-control' id='aciklama' name='aciklama' onChange={(e)=>setAciklama(e.target.value)}/>
            </div>
            <div className="mb-3">
              <label htmlFor="malzemeler">Malzemeler:{malzemeler.map(item=> <span key={item}>{item}</span> )}</label>
              <div className="input-group">
              <input type="text" name='malzeme' className='form-control'  onChange={(e)=>setMalzeme(e.target.value)}/>
              <button className='btn btn-secondary' type='button' onClick={handleAddMalzeme}>+</button>
              </div>
            </div>
            <div className="mb-3">
              <label htmlFor="hazirlanisi" className='form-label'>Hazirlanisi</label>
              <textarea name="hazirlanisi" id="hazirlanisi" className='form-control' onChange={(e)=>setHazirlanisi(e.target.value)}></textarea>
            </div>
            <div className="mb-3">
              <label htmlFor="resim" className='form-label'>Resim</label>
              <input type='text' name="resim" id="resim" className='form-control' onChange={(e)=>setResim(e.target.value)}></input>
            </div>
            <div className="mb-3">
              <label htmlFor="url" className='form-label'>Url</label>
              <input type='text' name="url" id="url" className='form-control' onChange={(e)=>setUrl(e.target.value)}></input>
            </div>
            <button type='submit' className='btn btn-primary' >Kaydet</button>
        </form>

      </div>
    </div>
  )
}

export default Create
